import React from 'react'
import Carousel from 'react-bootstrap/Carousel';
import 'bootstrap/dist/css/bootstrap.css';

const Banner = ({banner}) => {
  return (
    <Carousel>
        {banner.end.map((item, index) => (
                <Carousel.Item key={item.image} id="banner" interval={1000} keyboard={true}>
                    <img className="d-block w-100" id="banner" src={item.image} alt={`Product ${index}`} />
                    <Carousel.Caption>
                    <h3>{item.name}</h3>
                    <p>{item.description}</p>
                    <p>{item.source}</p>
                    <p style={{textDecoration:"underline", textDecorationColor:"white"}}> Read more</p>
                    </Carousel.Caption>
                </Carousel.Item>
        ))}
    </Carousel>
  )
}

export default Banner
